package clib.presentation.components.map.model

import klib.data.location.Location
import androidx.compose.ui.geometry.Size
import io.ktor.util.*

public data class Marker(
    val location: Location,
    val size: Size = Size(30f, 40f),
    val offset: Size = Size(-size.width / 2, -size.height),
    val image: ByteArray = Companion.image
) {
    public companion object Companion {
        public val image: ByteArray =
            "".decodeBase64Bytes()
    }

    override fun equals(other: Any?): Boolean {
        if (this === other) return true
        if (other == null || this::class != other::class) return false

        other as Marker

        if (location != other.location) return false
        if (!image.contentEquals(other.image)) return false

        return true
    }

    override fun hashCode(): Int {
        var result = location.hashCode()
        result = 31 * result + image.contentHashCode()
        return result
    }
}
